<?phpnamespace SHWPortfolioCatalog\Controllers\Admin;class AdminAssetsController{	public static function init() {		add_action('admin_enqueue_scripts', array(__CLASS__, 'adminStyles'));		add_action('admin_enqueue_scripts', array(__CLASS__, 'adminScripts'));	}	public static function adminStyles($hook){		wp_enqueue_style('addShortcode', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/shortcode_styles.css');		if ( $hook === \SHWPortfolioCatalog()->admin->pages['main_page'] || $hook === \SHWPortfolioCatalog()->admin->pages['categories'] || $hook === \SHWPortfolioCatalog()->admin->pages['shwthemesPage']){			wp_enqueue_style('shwcatalogAdminStyles', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/shwcatalog_portfoliomain.css');			wp_enqueue_style('shwcatalogAdminCategoryLoadingStyles', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/loading.css');			wp_enqueue_style('quickEdit', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/edit_image.css');			wp_enqueue_style('fontAwesome', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/font-awesome.min.css', false);			wp_enqueue_style('toastrjs', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/toastr.css');			wp_enqueue_style('editImage', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/quick_edit.css');		}		if ($hook === \SHWPortfolioCatalog()->admin->pages['main_page']) {			wp_enqueue_style('shwportfoliocatalog_modal', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/shwproduct-modal.css', false);		}		if($hook === \SHWPortfolioCatalog()->admin->pages['categories']) {			wp_enqueue_style('shwcatalogAdminCategoryStyles', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/category.css');		}		if( $hook === \SHWPortfolioCatalog()->admin->pages['shwthemesPage'] ) {//			wp_enqueue_style('shwproductmain', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/frontend/main.css');			wp_enqueue_style('shwthemestyles', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/css/admin/themes/main_theme.css');		}	}	public static function adminScripts($hook){		wp_enqueue_script('toastrjs', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/admin/toastr.min.js');		if ($hook === \SHWPortfolioCatalog()->admin->pages['main_page'] || $hook === \SHWPortfolioCatalog()->admin->pages['categories'] || $hook === \SHWPortfolioCatalog()->admin->pages['shwthemesPage'] ) {            wp_enqueue_media();			wp_enqueue_script('jquery');            wp_enqueue_script('jquery-ui-core');			if(isset($_GET['task'])) {				wp_enqueue_script('shwproduct_modal', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/admin/shwproduct_modal.js', array('jquery'), false, true);			}            wp_enqueue_script('shwcatalogAdminJs', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/admin/main.js', array('jquery', 'jquery-ui-core'), false, true);        }		if ($hook === \SHWPortfolioCatalog()->admin->pages['categories']) {			wp_enqueue_script('shwcatalogACategoryJs', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/admin/category.js', array('jquery', 'jquery-ui-core'), false, true);		}		if ($hook === \SHWPortfolioCatalog()->admin->pages['shwthemesPage']) {            wp_enqueue_script( 'shwcatalogFilterIso', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/frontend/templates/isotope.pkgd.min.js', array('jquery'), false, true);            wp_enqueue_script( 'masonry', \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/frontend/templates/masonry.js");            wp_enqueue_script("shwProductAjax", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/admin/theme/category.js", array('jquery'), false, true);            wp_enqueue_script('shwproduct_modal', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/admin/shwproduct_modal.js', array('jquery'), false, true);            wp_enqueue_script("shwbxslider", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/frontend/templates/bxslider.js", array('jquery'), false, true);            wp_enqueue_script( 'elevatezoom', \SHWPortfolioCatalog()->pluginUrl() . '/resources/assets/js/frontend/jquery.elevatezoom.js', array('jquery'), false, true);            wp_enqueue_script("shwtouchJs", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/frontend/jquery.touchSwipe.js", array('jquery'), false, true);            wp_enqueue_script("shwslick", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/frontend/templates/slick.min.js", array('jquery'), false, true);            wp_enqueue_script("filter", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/admin/theme/filter.js", array('jquery'), false, true);            wp_enqueue_script("main", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/admin/theme/main.js", array('jquery'), false, true);            wp_enqueue_script('bundleComponent', \SHWPortfolioCatalog()->pluginUrl() . '/static/bundle.js', false,false, true);        }		if (in_array($hook, array('post.php', 'post-new.php'))) {			wp_enqueue_script("shwproductInlinePopup", \SHWPortfolioCatalog()->pluginUrl() . "/resources/assets/js/admin/inline-popup.js", array('jquery'), false, true);		}		self::localizeScripts();	}	public static function localizeScripts(){		wp_localize_script('shwcatalogAdminJs', 'productSave', array(			'nonce' => wp_create_nonce('shwproduct_save_product'),		));		wp_localize_script('shwcatalogACategoryJs', 'addCategory', array(			'nonce' => wp_create_nonce('shwcatalog_add_category'),		));		wp_localize_script('shwproductInlinePopup', 'inlinePopup', array(			'nonce' => wp_create_nonce('shwproduct_save_shortcode_options'),		));		wp_localize_script('shwproductAdminProductSave', 'field', array (			'removeNonce' => wp_create_nonce('shwcatalog_remove_field'),			'duplicateNonce' => wp_create_nonce('shwcatalog_duplicate_field'),			'addCatalog' => wp_create_nonce('shwcatalog_add_catalog'),		));		wp_localize_script('shwcatalogSettings', 'settingsSave', array(			'nonce' => wp_create_nonce('shwcatalog_save_settings'),		));		wp_localize_script('shwcatalogAdminJs', 'form', array(			'removeNonce' => wp_create_nonce('shwcatalog_remove_form'),		));	}}